mapper.select:
SELECT category,
       price,
       brand
FROM tb_products
WHERE category = #{category}
  AND brand = #{brand}
ORDER BY price ASC LIMIT 1;

SELECT SUM(price)
FROM tb_products
WHERE brand = #{brand}

mapper.update:
UPDATE tb_products
SET category = #{updateCategory}
WHERE brand = #{brand}
  AND category = #{category}

UPDATE tb_products
SET price = #{price}
WHERE brand = #{brand}
  AND category = #{category}

mapper.delete:
DELETE
FROM tb_products
WHERE category = #{category}
  AND brand = #{brand}
